mapper.select:
select id, c_creator, c_request_start_time, c_ip, c_request_url, c_request_method,
c_user_agent, c_operation, c_request_finsh_time, c_return_time, c_return_data,
c_class_method_path,c_class_method_name,
c_params
from kbc_t_audit_log


        and c_creator like CONCAT('%', #{creator}, '%')


        and id =#{id}


         and c_request_start_time >= #{beginDate} 


         and c_request_start_time <= #{endDate}
